    private void btnAgPers1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnAgPers1MouseClicked
        modelo.Personal s = new modelo.Personal();
        /*Aplicar los set correspondientes al modelo*/
        s.setPrimerNombre(this.txtNombre1.getText());
        s.setSegundoNombre(this.txtNombre2.getText());
        s.setPrimerApellido(this.txtApellido1.getText());
        s.setSegundoApellido(this.txtApellido2.getText());
        
        Date date = txtfecha.getDate();
        date.getDate();
        long d = date.getTime();
        java.sql.Date fecha = new java.sql.Date(d);
        
        s.setFechaNacimiento(fecha);
        s.setDui(this.txtDui.getText());
        s.setNit(this.txtNit.getText());
        if (btnMasculino.isSelected() == true) {
            s.setGenero("Masculino");
        }
        else if(btnFemenino.isSelected() == true){
            s.setGenero("Femenino");
        }
        String cmb = (String) cmbEmpleado.getSelectedItem();
        s.setTipoPersonal(cmb);
        if (btnGenero.isSelected() == true) {
            s.setEstado("Activo");
        }
        else if (btnInactivo.isSelected() == true) {
            s.setEstado("Inactivo");
        }
        /*Cambiar el controlador LaborC*/
        if (controlador.PersonalC.isRegister(s)) {
            /*Cambiar el controlador LaborC*/
            controlador.PersonalC.setListar("");
            JOptionPane.showMessageDialog(this, "Exitoso");
        } else {
            JOptionPane.showMessageDialog(this, "Error");
        }
    }//GEN-LAST:event_btnAgPers1MouseClicked

